// List.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from '../../css/Boardlist.module.css'; // CSS 모듈 가져오기

const BoardList = () => {
  const [boards, setBoards] = useState([]); // 게시글 목록 상태

  useEffect(() => {
      axios.get('http://localhost:8080/spring/board/list') // 게시글 목록 API 호출
          .then(response => setBoards(response.data)) // 데이터가 성공적으로 반환되면 상태에 저장
          .catch(error => console.error('데이터를 불러오는 중 오류가 발생했습니다:', error));
  }, []);

  return (
      <div className={styles.container}>
          <h2 className={styles.title}>게시판 리스트</h2>
          <ul className={styles.list}>
              {boards.map(board => (
                  <li key={board.seq} className={styles.listItem}>
                      <h3 className={styles.postTitle}>{board.subject}</h3>
                      <p className={styles.postContent}>{board.content}</p>
                      <p className={styles.postInfo}>작성자: {board.name} | 조회수: {board.hit}</p>
                  </li>
              ))}
          </ul>
      </div>
  );
};

export default BoardList;
