import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

import Main from './components/main/Main';
import LoginForm from './components/member/LoginForm';
import Logout from './components/member/Logout';
import Write from './components/board/write';
import List from './components/board/list';
import styles from './css/style05.module.css';

const App01 = () => { 
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 로그인 상태 관리
  const navigate = useNavigate();

  useEffect(() => {
    // 서버에 로그인 상태를 확인하는 요청을 보냄
    axios.get('http://localhost:8080/spring/member/checkLogin')
      .then(res => {
        setIsLoggedIn(res.data === 'loggedIn');
      })
      .catch(error => {
        console.error("로그인 상태 확인 오류:", error);
      });
  }, []);

  const handleLogout = () => {
    // 로그아웃 요청을 서버에 보냄
    axios.get('http://localhost:8080/spring/member/logout')
      .then(() => {
        setIsLoggedIn(false);
        navigate('/'); // 로그아웃 후 메인화면으로 이동
      })
      .catch(error => {
        console.error("로그아웃 오류:", error);
      });
  };

  return (
    <BrowserRouter>
      <div className={styles.menunav}>
        <nav>
          <ul>
            <li> <Link to='/'>메인화면</Link></li>
            {!isLoggedIn ? (
              <li> <Link to='/loginForm'>LOGIN</Link></li>
            ) : (
              <li> <button onClick={handleLogout}>LOGOUT</button></li>
            )}
            <li> <Link to='/board/write'>글쓰기</Link></li>
            <li> <Link to='/board/list'>목록</Link></li>
          </ul>
        </nav>
      </div>
      <Routes>
        <Route path="/" element={<Main />} />  
        <Route path="/loginForm" element={<LoginForm />} />
        <Route path="/logout" element={<Logout />} />
        <Route path="/board/write" element={<Write />} />
        <Route path="/board/list" element={<List />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App01;
