import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from '../../css/BoardDetail.module.css';

const BoardDetail = () => {
     const { seq } = useParams(); 
     const [board, setBoard] = useState(null);
     const navigate = useNavigate();

     useEffect(() => {
          axios.get(`http://localhost:8080/spring/board/BoardDetail?seq=${seq}`)
          .then(res => setBoard(res.data))
          .catch(error => console.error('글을 불러오는 중 에러 : ', error));
     }, [seq]);

     if (!board) return <div>로딩 중..</div>

     return (
          <div className={styles.boardDetail}>
               <h2>{board.subject}</h2>
               <p>작성자 : { board.name }</p>
               <p>작성일 : { board.logtime }</p>
               <p>조회수 : { board.hit }</p>
               <div className={styles.content}>{board.content}</div> 
               <button onClick={() => navigate('/board/BoardList')}>목록</button>
          </div>
     );
};

export default BoardDetail;
