// BoardWriteForm.jsx
import React, { useState } from 'react';
import axios from 'axios';
import styles from '../../css/BoardWriteForm.module.css'; // CSS 모듈 가져오기

const BoardWriteForm = () => {
  const [subject, setSubject] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async () => {
      try {
          const boardData = { subject, content };
          const response = await axios.post('http://localhost:8080/board/write', boardData, {
              headers: {
                  'Content-Type': 'application/json',
              },
          });

          if (response.status === 200) {
              alert('게시물 작성 성공');
              setSubject('');
              setContent('');
          } else {
              alert('게시물 작성 실패');
          }
      } catch (error) {
          alert('게시물 작성에 실패');
      }
  };

  return (
      <form>
          <div>
              <label htmlFor="subject">제목:</label>
              <input
                  type="text"
                  id="subject"
                  value={subject}
                  onChange={(e) => setSubject(e.target.value)}
                  required
              />
          </div>
          <div>
              <label htmlFor="content">내용:</label>
              <textarea
                  id="content"
                  value={content}
                  onChange={(e) => setContent(e.target.value)}
                  required
              />
          </div>
          <button type="button" onClick={handleSubmit}>게시물 작성</button>
      </form>
  );
};

export default BoardWriteForm;
