// src/components/board/BoardList.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from '../../css/Boardlist.module.css'; // CSS 모듈 가져오기

const BoardList = () => {

  const [boards, setBoards] = useState([]); // 게시글 목록 상태
  const [loading, setLoading] = useState(true); // 로딩 상태
  const [error, setError] = useState(null); // 에러 상태

  useEffect(() => {
    // API 호출하여 게시글 목록 불러오기
    axios
      .get('http://localhost:8080/spring/board/list')
      .then(response => {
        setBoards(response.data); // 성공 시 게시글 목록 저장
        setLoading(false); // 로딩 상태 종료
      })
      .catch(error => {
        console.error('데이터를 불러오는 중 오류가 발생했습니다:', error);
        setError('데이터를 불러오는 중 오류가 발생했습니다.');
        setLoading(false); // 로딩 상태 종료
      });
  }, []);

  if (loading) return <div>로딩 중...</div>; // 로딩 중 메시지 표시
  if (error) return <div>{error}</div>; // 에러 메시지 표시

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>게시판 리스트</h2>
      {boards.length > 0 ? ( // 게시글이 있는 경우 목록 출력
        <ul className={styles.list}>
          {boards.map(board => (
            <li key={board.seq} className={styles.listItem}>
              <h3 className={styles.postTitle}>{board.subject}</h3>
              <p className={styles.postContent}>{board.content}</p>
              <p className={styles.postInfo}>
                작성자: {board.name} | 조회수: {board.hit}
              </p>
            </li>
          ))}
        </ul>
      ) : (
        <p className={styles.emptyMessage}>게시물이 없습니다.</p> // 게시물이 없는 경우 메시지 표시
      )}
    </div>
  );
};

export default BoardList;
