import React from 'react';
import data from '../data';  // data.jsx 파일에서 데이터 가져오기

import styles from '../../css/style05.module.css';

// Main 컴포넌트: 메인 페이지에 특정 제품 정보를 표시
const Main = () => {
  
  // products 배열에서 id가 '1'인 첫 번째 제품을 찾음
  const product = data.find((item) => item.id === '1');

  return (
    <div className={styles.main}>
      <h1>토심이의 홈페이지</h1>  
      <h2>방문해주셔서 감사합니다.</h2>  
      
      {/* product가 존재할 때만 아래 내용을 표시 */}
      {product && (
        <>
          <h3>{product.name}</h3>
          <img src={product.photo} alt={product.name} /> 
          <p>{product.description}</p>  
          <p>가격: {product.price} 원</p> 
        </>
      )}
    </div>
  );
};

export default Main;  // Main 컴포넌트를 다른 파일에서 사용할 수 있도록 내보내기
