// BoardWriteForm.jsx
import React, { useState } from 'react';
import axios from 'axios';
import styles from '../../css/BoardWriteForm.module.css'; // CSS 모듈 가져오기

const BoardWriteForm = () => {
  const [subject, setSubject] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async () => {
      try {
          const boardData = { subject, content };
          const response = await axios.post('http://localhost:8080/board/write', boardData, {
              headers: {
                  'Content-Type': 'application/json',
              },
          });

          if (response.status === 200) {
              alert('게시물 작성 성공');
              setSubject('');
              setContent('');
          } else {
              alert('게시물 작성 실패');
          }
      } catch (error) {
          alert('게시물 작성에 실패');
      }
  };

  return (
      <div className={styles.wrapper}>
          <div className={styles.container}>
              <h2 className={styles.title}>게시물 작성</h2>
              <form>
                  <table>
                      <thead></thead>
                      <tbody>
                          <tr>
                              <td>
                                  <label htmlFor="subject" className={styles.label}>제목:</label>
                              </td>
                              <td>
                                  <input
                                      type="text"
                                      id="subject"
                                      value={subject}
                                      onChange={(e) => setSubject(e.target.value)}
                                      required
                                      className={styles.input}
                                  />
                                  <div id='subjectDiv'></div>
                              </td>
                          </tr>
                          <tr>
                              <td>
                                  <label htmlFor="content" className={styles.label}>내용:</label>
                              </td>
                              <td>
                                  <textarea
                                      id="content"
                                      value={content}
                                      onChange={(e) => setContent(e.target.value)}
                                      required
                                      className={styles.textarea}
                                  />
                                  <div id='contentDiv'></div>
                              </td>
                          </tr>
                          <tr>
                              <td colSpan="2">
                                  <button type="button" onClick={handleSubmit} className={styles.button}>게시물 작성</button>
                              </td>
                          </tr>
                      </tbody>
                      <tfoot></tfoot>
                  </table>
              </form>
          </div>
      </div>
  );
};

export default BoardWriteForm;
