import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';

import Main from './components/main/Main';
import LoginForm from './components/member/LoginForm';
import BoardWriteForm from './components/board/BoardWriteForm';
import BoardList from './components/board/Boardlist';

import styles from './css/style05.module.css';

const App01 = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 로그인 상태

  useEffect(() => {
    // 로그인 상태 확인 API 호출
    axios.get('http://localhost:8080/spring/member/isLoggedIn')
      .then(response => setIsLoggedIn(response.data))
      .catch(error => console.error('로그인 상태 확인 중 오류 발생:', error));
  }, []);

  const Logout = () => {
    // 로그아웃 API 호출
    axios.get('http://localhost:8080/spring/member/logout')
      .then(() => {
        setIsLoggedIn(false);
        alert('로그아웃되었습니다.');
      })
      .catch(error => console.error('로그아웃 중 오류 발생:', error));
  };

  return (
    <BrowserRouter>
      <div className={styles.menunav}>
        <nav>
          <ul>
            <li><Link to="/">메인화면</Link></li>
            {isLoggedIn ? (
              <>
                <li><Link to="/board/BoardWriteForm">글쓰기</Link></li>
                <li><Link to="/board/Boardlist">목록</Link></li>
                <li><button onClick={ Logout } className={styles.logoutButton}>로그아웃</button></li>
              </>
            ) : (
              <>
                <li><Link to="/loginForm">LOGIN</Link></li>
                <li><Link to="/board/BoardWriteForm">글쓰기</Link></li>
                <li><Link to="/board/Boardlist">목록</Link></li>
              </>
            )}
          </ul>
        </nav>
      </div>
      <Routes>
        <Route path="/" element={<Main />} />
        <Route path="/loginForm" element={<LoginForm />} />
        <Route path="/board/BoardWriteForm" element={<BoardWriteForm />} />
        <Route path="/board/Boardlist" element={<BoardList />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App01;
